    /**\
     * This method calculates the Fibonacci term at the argument
     * pre: none
     * post: returns the desired term
     */
    private int getFibonacci(int num) {
        int b = num;
        if (b == 1 || b == 2) {
            return 1;
        } else {
            return (getFibonacci(b - 1) + getFibonacci(b - 2));
        }
    }
